import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./css/App.css";
import Registro from "./routes/unlogin/Registro";
import Login from "./routes/unlogin/Login";
import Home from "./routes/unlogin/Home";
import Session from "./session/Session";
import { Navbar, Container, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function App() {
    // Con esto se pone el login c:
    Session.setLogin(false);
    Session.setUser("usuario");

    // Con esto se obtiene el login c:
    let login = Session.isLogin();
    let user = Session.getUser();

    return (
        <BrowserRouter>        
            <Navbar bg="light" variant="light">
                <Container>
                    <Navbar.Brand className="">
                        <Link to="/">JADE</Link>
                    </Navbar.Brand>
                    <Nav className="me-auto navegacion-links">
                        <Nav.Link>Nosotros</Nav.Link>
                        <Nav.Link>Contacto</Nav.Link>
                        <Nav.Link>FAQ</Nav.Link>
                        {login === true ? (
                            <>
                                {user === "usuario" ? (
                                    <>
                                        <Nav.Link>Cursos</Nav.Link>
                                    </>
                                ) : (
                                    <>
                                        <Nav.Link>Gestionar alumnos</Nav.Link>
                                    </>
                                )}
                            </>
                        ) : (
                            <>
                                <Nav.Link>
                                    <Link to="/login">Iniciar sesión</Link>
                                </Nav.Link>
                                <Nav.Link>
                                    <Link to="/registrar">Registrarse</Link>
                                </Nav.Link>
                            </>
                        )}
                    </Nav>
                </Container>
            </Navbar>
            <Routes>
                <Route path="/" element={<Home />} />
                {login === true ? (
                    <>
                    </>
                ) : (
                    <>                        
                        <Route path="/login" element={<Login />} />
                        <Route path="/registrar" element={<Registro />} />
                    </>
                )}
            </Routes>
        </BrowserRouter>
    );
}
