const Session = {
    setLogin(login: boolean) {
        localStorage.setItem("login", login ? "1" : "0");
    },

    isLogin() {
        return localStorage.getItem("login") === "1";
    },
    getUser(): UserTypes {
        return localStorage.getItem("login") === "1" ? "usuario" : "admin";
    }
};
export type UserTypes = "usuario" | "admin";

export default Session;
